import { useState, useEffect } from 'react';
import './styles.css';

function Header() {
  return <header><h1>ExchangeRate</h1></header>;
}

// 货币选择组件
function CurrencySelect({ value, onChange, currencies }) {
  return (
    <select 
      className='CurrencySelect' 
      value={value} 
      onChange={onChange}
    >
      {currencies.map((currency) => (
        <option key={currency} value={currency}>
          {currency}
        </option>
      ))}
    </select>
  );
}

// 输入组件
function UserInput({ value, onChange, readOnly = false, type = "text" }) {
  return (
    <label>
      <input 
        className='CurrencyName'
        value={value}
        onChange={onChange}
        readOnly={readOnly}
        type={type}
      />
    </label>
  );
}

// 交换按钮组件
function ExchangeButton({ onClick }) {
  return (
    <button className='ExchangeButton' onClick={onClick}>
      <img src="https://gd-hbimg.huaban.com/936d8460afb48f0c0737186352ba325e0a7f7dc4641e-oRIMdv_fw658" alt="exchange"/>
    </button>
  );
}

// 加载指示器组件
function LoadingSpinner() {
  return <div className="loading-spinner"></div>;
}

// 错误消息组件
function ErrorMessage({ message }) {
  return <div className="error-message">{message}</div>;
}

function Converter() {
  // 状态管理
  const [fromCurrency, setFromCurrency] = useState("USD");
  const [toCurrency, setToCurrency] = useState("EUR");
  const [amount, setAmount] = useState("1");
  const [result, setResult] = useState("");
  const [date, setDate] = useState("");
  const [lastUpdate, setLastUpdate] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [currencies, setCurrencies] = useState([]);
  const [historicalRate, setHistoricalRate] = useState(null);

  // 获取可用货币列表
  useEffect(() => {
    const fetchCurrencies = async () => {
      try {
        const response = await fetch(
          `https://v6.exchangerate-api.com/v6/dd875ed8009b2e791ff987e6/latest/USD`
        );
        const data = await response.json();
        if (data.result === "success") {
          setCurrencies(Object.keys(data.conversion_rates));
        }
      } catch (error) {
        setError("Failed to load currencies");
      }
    };
    fetchCurrencies();
  }, []);

  // 获取当前汇率
  const fetchExchangeRate = async () => {
    setLoading(true);
    setError("");
    try {
      const response = await fetch(
        `https://v6.exchangerate-api.com/v6/dd875ed8009b2e791ff987e6/latest/${fromCurrency}`
      );
      const data = await response.json();

      if (data.result === "success") {
        const rate = data.conversion_rates[toCurrency];
        const calculatedResult = (parseFloat(amount) * rate).toFixed(2);
        setResult(calculatedResult);

        const updateDate = new Date(data.time_last_update_unix * 1000);
        setLastUpdate(updateDate.toLocaleDateString());
      } else {
        setError("Failed to get exchange rate");
      }
    } catch (error) {
      setError("Error fetching exchange rate");
    } finally {
      setLoading(false);
    }
  };

  // 获取历史汇率
  const fetchHistoricalRate = async (selectedDate) => {
    if (!selectedDate) return;

    setLoading(true);
    setError("");
    try {
      // 格式化日期为 YYYY-MM-DD
      const formattedDate = new Date(selectedDate).toISOString().split('T')[0];
      const response = await fetch(
        `https://v6.exchangerate-api.com/v6/dd875ed8009b2e791ff987e6/history/${fromCurrency}/${formattedDate}`
      );
      const data = await response.json();

      if (data.result === "success") {
        const historicalRate = data.conversion_rates[toCurrency];
        setHistoricalRate(historicalRate);
      } else {
        setError("Failed to get historical rate");
      }
    } catch (error) {
      setError("Error fetching historical rate");
    } finally {
      setLoading(false);
    }
  };

  // 当输入值改变时自动获取新的汇率
  useEffect(() => {
    if (amount && fromCurrency && toCurrency) {
      fetchExchangeRate();
    }
  }, [amount, fromCurrency, toCurrency]);

  // 处理金额输入变化
  const handleAmountChange = (e) => {
    const value = e.target.value;
    if (/^\d*\.?\d*$/.test(value)) {
      setAmount(value);
    }
  };

  // 处理日期输入变化
  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setDate(selectedDate);
    fetchHistoricalRate(selectedDate);
  };

  // 交换货币
  const handleExchange = () => {
    setFromCurrency(toCurrency);
    setToCurrency(fromCurrency);
  };

  return (
    <>
      <div className='Convertertitle'><h1>Converter</h1></div>
      {error && <ErrorMessage message={error} />}
      <div className='ConverterBody'>
        <div className="input-group">
          <UserInput 
            value={amount}
            onChange={handleAmountChange}
          />
          <CurrencySelect
            value={fromCurrency}
            onChange={(e) => setFromCurrency(e.target.value)}
            currencies={currencies}
          />
        </div>

        <ExchangeButton onClick={handleExchange} />

        <div className="input-group">
          <UserInput 
            value={result}
            readOnly={true}
          />
          <CurrencySelect
            value={toCurrency}
            onChange={(e) => setToCurrency(e.target.value)}
            currencies={currencies}
          />
        </div>

        <div className='ExchangeResult'>
          {loading ? <LoadingSpinner /> : (
            result && `${amount} ${fromCurrency} = ${result} ${toCurrency}`
          )}
        </div>

        <div className='Data'>
          <UserInput 
            type="date"
            value={date}
            onChange={handleDateChange}
          />
        </div>

        <div className='HistoricalRate'>
          {historicalRate && (
            `Historical Rate (${date}): 1 ${fromCurrency} = ${historicalRate} ${toCurrency}`
          )}
        </div>

        <div className='LastUpdateData'>
          {lastUpdate && `Last updated: ${lastUpdate}`}
        </div>
      </div>
    </>
  );
}

function LineChart() {
  return <h1>LineChart</h1>;
}

function ExchangeList() {
  return <h1>ExchangeList</h1>;
}

export default function App() {
  return (
    <>
      <div className='Header'><Header/></div>
      <div className='Converter'><Converter/></div>
      <div className='LineChart'><LineChart/></div>
      <div className='ExchangeList'><ExchangeList/></div>
    </>
  );
}
