function Header() {
  return <header><h1>ExchangeRate</h1></header>;
}

// Ë¥ßÂ∏ÅËæìÂÖ•ÂíåÈÄâÊã©ÁªÑ‰ª∂
function CurrencyInput({ value, onChange, currencies, selectedCurrency }) {
  const [inputValue, setInputValue] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);

    // ËøáÊª§ÂåπÈÖçÁöÑË¥ßÂ∏Å
    const matches = currencies.filter(currency => 
      currency.toLowerCase().includes(value.toLowerCase())
    );
    setSuggestions(matches);
  };

  const handleSelect = (currency) => {
    setInputValue('');
    onChange(currency);
    setSuggestions([]);
  };

  return (
    <div className="currency-input-container">
      <input
        className="currency-search"
        value={inputValue}
        onChange={handleInputChange}
        placeholder="Search currency..."
      />
      {suggestions.length > 0 && (
        <div className="currency-suggestions">
          {suggestions.map(currency => (
            <div
              key={currency}
              className="currency-suggestion-item"
              onClick={() => handleSelect(currency)}
            >
              {currencyInfo[currency]?.flag} {currency} - {currencyInfo[currency]?.name}
            </div>
          ))}
        </div>
      )}
      <div className="selected-currency">
        {currencyInfo[selectedCurrency]?.flag} {selectedCurrency} - {currencyInfo[selectedCurrency]?.name}
      </div>
    </div>
  );
}

