function LineChart({ fromCurrency, toCurrency }) {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchHistoricalData = async () => {
      // Ëé∑ÂèñËøáÂéª30Â§©ÁöÑÊï∞ÊçÆ
      const endDate = new Date();
      const startDate = new Date();
      startDate.setDate(startDate.getDate() - 30);

      // ËøôÈáåÈúÄË¶ÅÊ†πÊçÆÊÇ®ÁöÑAPIË∞ÉÊï¥
      const response = await fetch(`your_historical_data_endpoint`);
      const data = await response.json();

      setChartData({
        labels: data.dates,
        datasets: [{
          label: `${fromCurrency} to ${toCurrency} Exchange Rate`,
          data: data.rates,
          fill: false,
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.1
        }]
      });
    };

    if (fromCurrency && toCurrency) {
      fetchHistoricalData();
    }
  }, [fromCurrency, toCurrency]);

  return (
    <div className="line-chart-container">
      <h2>Exchange Rate Trend</h2>
      {chartData && <Line data={chartData} />}
    </div>
  );
}

