import React, { useState, useEffect } from 'react';
import exchangeIcon from './ExchangeButton.png';
import './styles.css';

const CURRENCY_FLAGS = {
  USD: "ðŸ‡ºðŸ‡¸",
  GBP: "ðŸ‡¬ðŸ‡§",
  EUR: "ðŸ‡ªðŸ‡º",
  CNY: "ðŸ‡¨ðŸ‡³",
  CAD: "ðŸ‡¨ðŸ‡¦",
  JPY: "ðŸ‡¯ðŸ‡µ",
  AUD: "ðŸ‡¦ðŸ‡º",
  NZD: "ðŸ‡³ðŸ‡¿",
  CHF: "ðŸ‡¨ðŸ‡­",
  HKD: "ðŸ‡­ðŸ‡°",
  SGD: "ðŸ‡¸ðŸ‡¬",
  SEK: "ðŸ‡¸ðŸ‡ª",
  KRW: "ðŸ‡°ðŸ‡·",
  INR: "ðŸ‡®ðŸ‡³",
  MXN: "ðŸ‡²ðŸ‡½",
};

const CURRENCY_NAMES = {
  USD: "United States Dollar",
  GBP: "British Pound Sterling",
  EUR: "Euro",
  CNY: "Chinese Yuan",
  CAD: "Canadian Dollar",
  JPY: "Japanese Yen",
  AUD: "Australian Dollar",
  NZD: "New Zealand Dollar",
  CHF: "Swiss Franc",
  HKD: "Hong Kong Dollar",
  SGD: "Singapore Dollar",
  SEK: "Swedish Krona",
  KRW: "South Korean Won",
  INR: "Indian Rupee",
  MXN: "Mexican Peso",
};

function App() {
  const [rates, setRates] = useState({});
  const [fromCurrency, setFromCurrency] = useState("USD");
  const [toCurrency, setToCurrency] = useState("EUR");
  const [amount, setAmount] = useState(1);
  const [fromInput, setFromInput] = useState(`USD - ${CURRENCY_NAMES.USD}`);
  const [toInput, setToInput] = useState(`EUR - ${CURRENCY_NAMES.EUR}`);
  const [historicalData, setHistoricalData] = useState([]);
  const [showFromDropdown, setShowFromDropdown] = useState(false);
  const [showToDropdown, setShowToDropdown] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);

  useEffect(() => {
    fetchRates();
    generateMockHistoricalData();
  }, [selectedDate]); // æ·»åŠ selectedDateä½œä¸ºä¾èµ–

  const fetchRates = async () => {
    try {
      // åœ¨å®žé™…åº”ç”¨ä¸­ï¼Œè¿™é‡Œåº”è¯¥æ ¹æ®selectedDateèŽ·å–åŽ†å²æ±‡çŽ‡
      const response = await fetch('https://v6.exchangerate-api.com/v6/dd875ed8009b2e791ff987e6/latest/USD');
      const data = await response.json();
      setRates(data.conversion_rates);
    } catch (error) {
      console.error('Error fetching rates:', error);
    }
  };

  const generateMockHistoricalData = () => {
    const data = [];
    const today = new Date();
    for (let i = 30; i >= 0; i--) {
      const date = new Date(today);
      date.setDate(date.getDate() - i);
      data.push({
        date: date.toISOString().split('T')[0],
        rate: 1 + Math.random() * 0.2,
      });
    }
    setHistoricalData(data);
  };

  const convertCurrency = () => {
    if (!rates[fromCurrency] || !rates[toCurrency]) return 0;
    const rate = rates[toCurrency] / rates[fromCurrency];
    return (amount * rate).toFixed(6);
  };

  const filterCurrencies = (search) => {
    if (!search) return Object.keys(CURRENCY_NAMES);
    return Object.keys(CURRENCY_NAMES).filter(code =>
      code.toLowerCase().includes(search.toLowerCase()) ||
      CURRENCY_NAMES[code].toLowerCase().includes(search.toLowerCase())
    );
  };

  const handleFromCurrencySelect = (code) => {
    setFromCurrency(code);
    setFromInput(`${code} - ${CURRENCY_NAMES[code]}`);
    setShowFromDropdown(false);
  };

  const handleToCurrencySelect = (code) => {
    setToCurrency(code);
    setToInput(`${code} - ${CURRENCY_NAMES[code]}`);
    setShowToDropdown(false);
  };

  const handleFromInputChange = (e) => {
    const value = e.target.value;
    setFromInput(value);
    setShowFromDropdown(true);
  };

  const handleToInputChange = (e) => {
    const value = e.target.value;
    setToInput(value);
    setShowToDropdown(true);
  };

  const handleExchange = () => {
    const tempCurrency = fromCurrency;
    const tempInput = fromInput;
    setFromCurrency(toCurrency);
    setFromInput(toInput);
    setToCurrency(tempCurrency);
    setToInput(tempInput);
  };

  return (
    <div className="app">
      <h1>Exchange Rate</h1>

      <div className="main-container">
        <div className="left-panel">
          <div className="converter-container">
            <h2>Converter</h2>
            <div className="currency-inputs">
              <div className="currency-row">
                <div className="currency-input">
                  <input
                    type="number"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    placeholder="Amount"
                  />
                  <div className="currency-select">
                    <input
                      type="text"
                      value={fromInput}
                      onChange={handleFromInputChange}
                      onClick={() => setShowFromDropdown(true)}
                      placeholder="From Currency"
                    />
                    {showFromDropdown && (
                      <div className="dropdown">
                        {filterCurrencies(fromInput).map(code => (
                          <div
                            key={code}
                            className="dropdown-item"
                            onClick={() => handleFromCurrencySelect(code)}
                          >
                            {CURRENCY_FLAGS[code]} {code} - {CURRENCY_NAMES[code]}
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                </div>

                <button className="exchange-button" onClick={handleExchange}>
                  <img src={exchangeIcon} alt="Exchange" />
                </button>

                <div className="currency-input">
                  <input
                    type="text"
                    value={convertCurrency()}
                    readOnly
                    placeholder="Converted Amount"
                  />
                  <div className="currency-select">
                    <input
                      type="text"
                      value={toInput}
                      onChange={handleToInputChange}
                      onClick={() => setShowToDropdown(true)}
                      placeholder="To Currency"
                    />
                    {showToDropdown && (
                      <div className="dropdown">
                        {filterCurrencies(toInput).map(code => (
                          <div
                            key={code}
                            className="dropdown-item"
                            onClick={() => handleToCurrencySelect(code)}
                          >
                            {CURRENCY_FLAGS[code]} {code} - {CURRENCY_NAMES[code]}
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              </div>

              <div className="date-selector">
                <input
                  type="date"
                  value={selectedDate}
                  onChange={(e) => setSelectedDate(e.target.value)}
                  max={new Date().toISOString().split('T')[0]}
                />
              </div>
            </div>
          </div>

          <div className="chart-container">
            <h2>Exchange Rate History</h2>
            <div className="chart">
              <svg viewBox="0 0 800 400">
                {historicalData.map((point, index) => {
                  const x = (index / (historicalData.length - 1)) * 760 + 20;
                  const y = 380 - (point.rate - 1) * 1000;
                  return (
                    <React.Fragment key={index}>
                      {index > 0 && (
                        <line
                          x1={(((index - 1) / (historicalData.length - 1)) * 760) + 20}
                          y1={380 - (historicalData[index - 1].rate - 1) * 1000}
                          x2={x}
                          y2={y}
                          stroke="#007bff"
                          strokeWidth="2"
                        />
                      )}
                      <circle
                        cx={x}
                        cy={y}
                        r="4"
                        fill="#007bff"
                      />
                    </React.Fragment>
                  );
                })}
              </svg>
            </div>
          </div>
        </div>

        <div className="watchlist-container">
          <h2>Exchange Rate Watchlist</h2>
          {Object.keys(CURRENCY_FLAGS).slice(0, 6).map(currency => (
            <div key={currency} className="watchlist-item">
              <span>{CURRENCY_FLAGS[currency]} {currency}</span>
              <span>{rates[currency]?.toFixed(4) || "N/A"}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
