import React, { useState, useEffect } from 'react';
import exchangeIcon from './ExchangeButton.png';
import './styles.css';

const CURRENCY_FLAGS = {
  USD: "ðŸ‡ºðŸ‡¸",
  EUR: "ðŸ‡ªðŸ‡º",
  GBP: "ðŸ‡¬ðŸ‡§",
  JPY: "ðŸ‡¯ðŸ‡µ",
  AUD: "ðŸ‡¦ðŸ‡º",
  CAD: "ðŸ‡¨ðŸ‡¦",
  CHF: "ðŸ‡¨ðŸ‡­",
  CNY: "ðŸ‡¨ðŸ‡³",
  HKD: "ðŸ‡­ðŸ‡°",
  NZD: "ðŸ‡³ðŸ‡¿"
};

const CURRENCY_NAMES = {
  USD: "US Dollar",
  EUR: "Euro",
  GBP: "British Pound",
  JPY: "Japanese Yen",
  AUD: "Australian Dollar",
  CAD: "Canadian Dollar",
  CHF: "Swiss Franc",
  CNY: "Chinese Yuan",
  HKD: "Hong Kong Dollar",
  NZD: "New Zealand Dollar"
};

function App() {
  const [rates, setRates] = useState({});
  const [fromCurrency, setFromCurrency] = useState("USD");
  const [toCurrency, setToCurrency] = useState("EUR");
  const [amount, setAmount] = useState(1);
  const [fromInput, setFromInput] = useState(`USD - ${CURRENCY_NAMES.USD}`);
  const [toInput, setToInput] = useState(`EUR - ${CURRENCY_NAMES.EUR}`);
  const [historicalData, setHistoricalData] = useState([]);
  const [showFromDropdown, setShowFromDropdown] = useState(false);
  const [showToDropdown, setShowToDropdown] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [lastUpdated, setLastUpdated] = useState('');

  useEffect(() => {
    fetchRates();
    generateMockHistoricalData();
  }, [selectedDate]);

  const fetchRates = async () => {
    try {
      const response = await fetch('https://v6.exchangerate-api.com/v6/dd875ed8009b2e791ff987e6/latest/USD');
      const data = await response.json();
      if (data.result === "success") {
        setRates(data.conversion_rates);
        setLastUpdated(new Date(data.time_last_update_utc).toLocaleTimeString());
      }
    } catch (error) {
      console.error("Error fetching rates:", error);
    }
  };

  const generateMockHistoricalData = () => {
    const data = [];
    const baseValue = rates[toCurrency] || 1.15;
    const numPoints = 30;

    for (let i = 0; i < numPoints; i++) {
      const date = new Date();
      date.setDate(date.getDate() - (numPoints - i));
      const variation = (Math.random() - 0.5) * 0.1;
      const rate = baseValue + variation;

      data.push({
        date: date.toISOString().split('T')[0],
        rate: rate
      });
    }

    setHistoricalData(data);
  };

  const handleFromInputChange = (e) => {
    const value = e.target.value;
    setFromInput(value);

    const matchingCurrency = Object.entries(CURRENCY_NAMES).find(([code, name]) => 
      `${code} - ${name}`.toLowerCase().includes(value.toLowerCase())
    );

    if (matchingCurrency) {
      setFromCurrency(matchingCurrency[0]);
    }
  };

  const handleToInputChange = (e) => {
    const value = e.target.value;
    setToInput(value);

    const matchingCurrency = Object.entries(CURRENCY_NAMES).find(([code, name]) => 
      `${code} - ${name}`.toLowerCase().includes(value.toLowerCase())
    );

    if (matchingCurrency) {
      setToCurrency(matchingCurrency[0]);
    }
  };

  const handleFromCurrencySelect = (code) => {
    setFromCurrency(code);
    setFromInput(`${code} - ${CURRENCY_NAMES[code]}`);
    setShowFromDropdown(false);
  };

  const handleToCurrencySelect = (code) => {
    setToCurrency(code);
    setToInput(`${code} - ${CURRENCY_NAMES[code]}`);
    setShowToDropdown(false);
  };

  const handleExchange = () => {
    const temp = fromCurrency;
    setFromCurrency(toCurrency);
    setToCurrency(temp);

    const tempInput = fromInput;
    setFromInput(toInput);
    setToInput(tempInput);
  };

  const filterCurrencies = (input) => {
    return Object.keys(CURRENCY_NAMES).filter(code => 
      `${code} - ${CURRENCY_NAMES[code]}`.toLowerCase().includes(input.toLowerCase())
    );
  };

  const convertCurrency = () => {
    if (!rates[fromCurrency] || !rates[toCurrency] || !amount) {
      return "";
    }
    const conversion = (amount * rates[toCurrency]) / rates[fromCurrency];
    return `${conversion.toFixed(2)} ${toCurrency}`;
  };

  return (
    <div className="app">
      <h1>Exchange Rate</h1>

      <div className="main-container">
        <div className="left-panel">
          <div className="converter-container">
            <h2>Converter</h2>
            <div className="currency-inputs">
              <div className="currency-row">
                <div className="currency-input">
                  <div className="currency-select">
                    <input
                      type="text"
                      value={fromInput}
                      onChange={handleFromInputChange}
                      onClick={() => setShowFromDropdown(true)}
                      placeholder="From Currency"
                    />
                    {showFromDropdown && (
                      <div className="dropdown">
                        {filterCurrencies(fromInput).map(code => (
                          <div
                            key={code}
                            className="dropdown-item"
                            onClick={() => handleFromCurrencySelect(code)}
                          >
                            {CURRENCY_FLAGS[code]} {code} - {CURRENCY_NAMES[code]}
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                </div>

                <button className="exchange-button" onClick={handleExchange}>
                  <img src={exchangeIcon} alt="Exchange" />
                </button>

                <div className="currency-input">
                  <div className="currency-select">
                    <input
                      type="text"
                      value={toInput}
                      onChange={handleToInputChange}
                      onClick={() => setShowToDropdown(true)}
                      placeholder="To Currency"
                    />
                    {showToDropdown && (
                      <div className="dropdown">
                        {filterCurrencies(toInput).map(code => (
                          <div
                            key={code}
                            className="dropdown-item"
                            onClick={() => handleToCurrencySelect(code)}
                          >
                            {CURRENCY_FLAGS[code]} {code} - {CURRENCY_NAMES[code]}
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              </div>

              <div className="amount-row">
                <div className="amount-input">
                  <input
                    type="number"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    placeholder="Amount"
                  />
                </div>
                <div className="amount-result">
                  <input
                    type="text"
                    value={convertCurrency()}
                    readOnly
                    placeholder="Converted Amount"
                  />
                </div>
              </div>

              <div className="date-row">
                <div className="date-input">
                  <input
                    type="date"
                    value={selectedDate}
                    onChange={(e) => setSelectedDate(e.target.value)}
                    max={new Date().toISOString().split('T')[0]}
                  />
                </div>
                <div className="last-updated">
                  Last updated: {lastUpdated}
                </div>
              </div>
            </div>
          </div>

          <div className="chart-container">
            <h2>Historical Rates</h2>
            <div className="chart">
              <svg>
                {/* SVG chart implementation */}
              </svg>
            </div>
          </div>
        </div>

        <div className="watchlist-container">
          <h2>Watchlist</h2>
          <div className="watchlist-items">
            {Object.keys(CURRENCY_NAMES).slice(0, 5).map(code => (
              <div key={code} className="watchlist-item">
                <span>{CURRENCY_FLAGS[code]} {code}</span>
                <span>{(rates[code] || 0).toFixed(2)}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
