import React, { useState, useEffect, useRef } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { translations } from './translations';
import './styles.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

// LanguageSelector Component
const LanguageSelector = ({ currentLanguage, setCurrentLanguage, translations }) => (
  <div className="language-selector">
    {Object.keys(translations).map((lang) => (
      <button
        key={lang}
        className={`language-button ${currentLanguage === lang ? 'active' : ''}`}
        onClick={() => setCurrentLanguage(lang)}
      >
        {translations[lang].languages[lang]}
      </button>
    ))}
  </div>
);

// CurrencyInput Component
const CurrencyInput = ({ 
  inputValue,
  onInputChange,
  showDropdown,
  onFocus,
  placeholder,
  dropdownRef,
  filteredCurrencies,
  onCurrencySelect,
  translations 
}) => (
  <div className="currency-input">
    <div className="currency-select" ref={dropdownRef}>
      <input
        type="text"
        value={inputValue}
        onChange={onInputChange}
        onFocus={onFocus}
        placeholder={placeholder}
      />
      {showDropdown && (
        <div className="currency-dropdown">
          {filteredCurrencies.map(currency => (
            <div
              key={currency}
              className="currency-option"
              onClick={() => onCurrencySelect(currency)}
            >
              {currency} - {translations.currencyNames[currency]}
            </div>
          ))}
        </div>
      )}
    </div>
  </div>
);

// CurrencyConverter Component
const CurrencyConverter = ({
  fromDropdownRef,
  toDropdownRef,
  fromInput,
  toInput,
  handleFromInputChange,
  handleToInputChange,
  showFromDropdown,
  showToDropdown,
  setShowFromDropdown,
  setShowToDropdown,
  filteredFromCurrencies,
  filteredToCurrencies,
  handleFromCurrencySelect,
  handleToCurrencySelect,
  amount,
  handleAmountChange,
  convertAmount,
  selectedDate,
  handleDateChange,
  lastUpdated,
  t
}) => (
  <div className="converter-container">
    <div className="currency-inputs">
      <div className="currency-row">
        <CurrencyInput
          inputValue={fromInput}
          onInputChange={handleFromInputChange}
          showDropdown={showFromDropdown}
          onFocus={() => setShowFromDropdown(true)}
          placeholder={t.selectCurrency}
          dropdownRef={fromDropdownRef}
          filteredCurrencies={filteredFromCurrencies}
          onCurrencySelect={handleFromCurrencySelect}
          translations={t}
        />

        <CurrencyInput
          inputValue={toInput}
          onInputChange={handleToInputChange}
          showDropdown={showToDropdown}
          onFocus={() => setShowToDropdown(true)}
          placeholder={t.selectCurrency}
          dropdownRef={toDropdownRef}
          filteredCurrencies={filteredToCurrencies}
          onCurrencySelect={handleToCurrencySelect}
          translations={t}
        />
      </div>

      <div className="amount-row">
        <div className="amount-input">
          <input
            type="number"
            value={amount}
            onChange={handleAmountChange}
            placeholder={t.amount}
          />
        </div>
        <div className="amount-result">
          <input
            type="text"
            value={convertAmount()}
            readOnly
            placeholder={t.convertedAmount}
          />
        </div>
      </div>

      <div className="date-row">
        <div className="date-input">
          <input
            type="date"
            value={selectedDate}
            onChange={handleDateChange}
          />
        </div>
        <span className="last-updated">
          {t.lastUpdated}: {lastUpdated}
        </span>
      </div>
    </div>
  </div>
);

// HistoricalChart Component
const HistoricalChart = ({ historicalRates, chartOptions, fromCurrency, toCurrency, t }) => (
  <div className="chart-container">
    <h2>{t.historicalRates}: {fromCurrency} {t.to} {toCurrency}</h2>
    {historicalRates.datasets && (
      <Line data={historicalRates} options={chartOptions} />
    )}
  </div>
);

// Watchlist Component
const Watchlist = ({ currencies, currencyRates, t }) => (
  <div className="watchlist-container">
    <h2>{t.popularCurrencies}</h2>
    <div className="watchlist">
      {currencies.map(currency => (
        <div key={currency} className="watchlist-item">
          <span className="currency-code">{currency}</span>
          <span className="currency-name">{t.currencyNames[currency]}</span>
          <span className="currency-rate">
            {currencyRates[currency] ? 
              `1 USD = ${currencyRates[currency].toFixed(4)} ${currency}` 
              : 'Loading...'}
          </span>
        </div>
      ))}
    </div>
  </div>
);

// Main App Component
function App() {
  const [fromCurrency, setFromCurrency] = useState('USD');
  const [toCurrency, setToCurrency] = useState('EUR');
  const [amount, setAmount] = useState(1);
  const [exchangeRate, setExchangeRate] = useState(null);
  const [lastUpdated, setLastUpdated] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [historicalRates, setHistoricalRates] = useState([]);
  const [fromInput, setFromInput] = useState('USD');
  const [toInput, setToInput] = useState('EUR');
  const [showFromDropdown, setShowFromDropdown] = useState(false);
  const [showToDropdown, setShowToDropdown] = useState(false);
  const [currentLanguage, setCurrentLanguage] = useState('en');
  const [currencyRates, setCurrencyRates] = useState({});

  const t = translations[currentLanguage];

  const currencies = [
    'USD', 'EUR', 'GBP', 'JPY', 'AUD', 'CAD', 'CHF', 'CNY', 'HKD', 'NZD',
    'AED', 'INR', 'SGD', 'MXN', 'BRL'
  ];

  const fromDropdownRef = useRef(null);
  const toDropdownRef = useRef(null);

  // ... 保持所有的 useEffect 和其他函数不变 ...

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Exchange Rate Trend'
      }
    },
    scales: {
      y: {
        beginAtZero: false
      }
    }
  };

  return (
    <div className="app">
      <LanguageSelector 
        currentLanguage={currentLanguage}
        setCurrentLanguage={setCurrentLanguage}
        translations={translations}
      />
      <h1>{t.title}</h1>

      <div className="main-container">
        <div className="left-panel">
          <CurrencyConverter
            fromDropdownRef={fromDropdownRef}
            toDropdownRef={toDropdownRef}
            fromInput={fromInput}
            toInput={toInput}
            handleFromInputChange={handleFromInputChange}
            handleToInputChange={handleToInputChange}
            showFromDropdown={showFromDropdown}
            showToDropdown={showToDropdown}
            setShowFromDropdown={setShowFromDropdown}
            setShowToDropdown={setShowToDropdown}
            filteredFromCurrencies={filteredFromCurrencies}
            filteredToCurrencies={filteredToCurrencies}
            handleFromCurrencySelect={handleFromCurrencySelect}
            handleToCurrencySelect={handleToCurrencySelect}
            amount={amount}
            handleAmountChange={handleAmountChange}
            convertAmount={convertAmount}
            selectedDate={selectedDate}
            handleDateChange={handleDateChange}
            lastUpdated={lastUpdated}
            t={t}
          />

          <HistoricalChart
            historicalRates={historicalRates}
            chartOptions={chartOptions}
            fromCurrency={fromCurrency}
            toCurrency={toCurrency}
            t={t}
          />
        </div>

        <Watchlist 
          currencies={currencies}
          currencyRates={currencyRates}
          t={t}
        />
      </div>
    </div>
  );
}

export default App;
