import React, { useState, useEffect } from 'react';
import exchangeIcon from './ExchangeButton.png';
import './styles.css';

const CURRENCY_FLAGS = {
  USD: "üá∫üá∏",
  EUR: "üá™üá∫",
  GBP: "üá¨üáß",
  JPY: "üáØüáµ",
  AUD: "üá¶üá∫",
  CAD: "üá®üá¶",
  CHF: "üá®üá≠",
  CNY: "üá®üá≥",
  HKD: "üá≠üá∞",
  NZD: "üá≥üáø"
};

const CURRENCY_NAMES = {
  USD: "US Dollar",
  EUR: "Euro",
  GBP: "British Pound",
  JPY: "Japanese Yen",
  AUD: "Australian Dollar",
  CAD: "Canadian Dollar",
  CHF: "Swiss Franc",
  CNY: "Chinese Yuan",
  HKD: "Hong Kong Dollar",
  NZD: "New Zealand Dollar"
};

function LineChart({ data, width = 600, height = 300 }) {
  const padding = { top: 20, right: 30, bottom: 30, left: 50 };
  const chartWidth = width - padding.left - padding.right;
  const chartHeight = height - padding.top - padding.bottom;

  // Find min and max values for scaling
  const minRate = Math.min(...data.map(d => d.rate));
  const maxRate = Math.max(...data.map(d => d.rate));
  const dates = data.map(d => new Date(d.date));
  const minDate = Math.min(...dates);
  const maxDate = Math.max(...dates);

  // Scale functions
  const xScale = (date) => {
    return padding.left + ((new Date(date) - minDate) / (maxDate - minDate)) * chartWidth;
  };

  const yScale = (rate) => {
    return height - padding.bottom - ((rate - minRate) / (maxRate - minRate)) * chartHeight;
  };

  // Generate path data
  const pathData = data.map((point, index) => {
    const x = xScale(point.date);
    const y = yScale(point.rate);
    return index === 0 ? `M ${x},${y}` : `L ${x},${y}`;
  }).join(' ');

  // Generate x-axis ticks
  const xTicks = data.filter((_, i) => i % 5 === 0).map(point => ({
    x: xScale(point.date),
    label: new Date(point.date).toLocaleDateString()
  }));

  // Generate y-axis ticks
  const yTickCount = 5;
  const yTicks = Array.from({ length: yTickCount }).map((_, i) => {
    const rate = minRate + (maxRate - minRate) * (i / (yTickCount - 1));
    return {
      y: yScale(rate),
      label: rate.toFixed(2)
    };
  });

  return (
    <svg width={width} height={height}>
      {/* X axis */}
      <line
        x1={padding.left}
        y1={height - padding.bottom}
        x2={width - padding.right}
        y2={height - padding.bottom}
        stroke="black"
      />

      {/* Y axis */}
      <line
        x1={padding.left}
        y1={padding.top}
        x2={padding.left}
        y2={height - padding.bottom}
        stroke="black"
      />

      {/* X axis ticks */}
      {xTicks.map((tick, i) => (
        <g key={i}>
          <line
            x1={tick.x}
            y1={height - padding.bottom}
            x2={tick.x}
            y2={height - padding.bottom + 5}
            stroke="black"
          />
          <text
            x={tick.x}
            y={height - padding.bottom + 20}
            textAnchor="middle"
            fontSize="12"
          >
            {tick.label}
          </text>
        </g>
      ))}

      {/* Y axis ticks */}
      {yTicks.map((tick, i) => (
        <g key={i}>
          <line
            x1={padding.left - 5}
            y1={tick.y}
            x2={padding.left}
            y2={tick.y}
            stroke="black"
          />
          <text
            x={padding.left - 10}
            y={tick.y}
            textAnchor="end"
            alignmentBaseline="middle"
            fontSize="12"
          >
            {tick.label}
          </text>
        </g>
      ))}

      {/* Line chart */}
      <path
        d={pathData}
        fill="none"
        stroke="blue"
        strokeWidth="2"
      />
    </svg>
  );
}

function App() {
  const [rates, setRates] = useState({});
  const [fromCurrency, setFromCurrency] = useState("USD");
  const [toCurrency, setToCurrency] = useState("EUR");
  const [amount, setAmount] = useState(1);
  const [fromInput, setFromInput] = useState(`USD - ${CURRENCY_NAMES.USD}`);
  const [toInput, setToInput] = useState(`EUR - ${CURRENCY_NAMES.EUR}`);
  const [historicalData, setHistoricalData] = useState([]);
  const [showFromDropdown, setShowFromDropdown] = useState(false);
  const [showToDropdown, setShowToDropdown] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [lastUpdated, setLastUpdated] = useState('');

  useEffect(() => {
    fetchRates();
    generateHistoricalData();
  }, [selectedDate, toCurrency]);

  const fetchRates = async () => {
    try {
      const response = await fetch('https://v6.exchangerate-api.com/v6/dd875ed8009b2e791ff987e6/latest/USD');
      const data = await response.json();
      if (data.result === "success") {
        setRates(data.conversion_rates);
        setLastUpdated(new Date(data.time_last_update_utc).toLocaleTimeString());
      }
    } catch (error) {
      console.error("Error fetching rates:", error);
    }
  };

  const generateHistoricalData = () => {
    const data = [];
    const baseValue = rates[toCurrency] || 1.15;
    const numPoints = 30;

    for (let i = 0; i < numPoints; i++) {
      const date = new Date();
      date.setDate(date.getDate() - (numPoints - i));
      const variation = (Math.random() - 0.5) * 0.1;
      const rate = baseValue + variation;

      data.push({
        date: date.toISOString().split('T')[0],
        rate: rate
      });
    }

    setHistoricalData(data);
  };

  // ... [ÂÖ∂‰Ωô‰ª£Á†Å‰∏é‰πãÂâçÁõ∏ÂêåÔºå‰øùÊåÅ‰∏çÂèò]

  return (
    <div className="app">
      <h1>Exchange Rate</h1>

      <div className="main-container">
        <div className="left-panel">
          <div className="converter-container">
            {/* ... [ËΩ¨Êç¢Âô®ÈÉ®ÂàÜ‰ª£Á†Å‰∏é‰πãÂâçÁõ∏Âêå] ... */}
          </div>

          <div className="chart-container">
            <h2>Historical Rates</h2>
            <div className="chart">
              <LineChart data={historicalData} />
            </div>
          </div>
        </div>

        <div className="watchlist-container">
          <h2>Watchlist</h2>
          <div className="watchlist-items">
            {Object.keys(CURRENCY_NAMES).slice(0, 5).map(code => (
              <div key={code} className="watchlist-item">
                <span>{CURRENCY_FLAGS[code]} {code}</span>
                <span>{(rates[code] || 0).toFixed(2)}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
