import React, { useState, useEffect, useRef } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { translations } from './translations';
import './styles.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const CURRENCY_INFO = {
  USD: { flag: "ðŸ‡ºðŸ‡¸", name: "US Dollar" },
  EUR: { flag: "ðŸ‡ªðŸ‡º", name: "Euro" },
  GBP: { flag: "ðŸ‡¬ðŸ‡§", name: "British Pound" },
  JPY: { flag: "ðŸ‡¯ðŸ‡µ", name: "Japanese Yen" },
  AUD: { flag: "ðŸ‡¦ðŸ‡º", name: "Australian Dollar" },
  CAD: { flag: "ðŸ‡¨ðŸ‡¦", name: "Canadian Dollar" },
  CHF: { flag: "ðŸ‡¨ðŸ‡­", name: "Swiss Franc" },
  CNY: { flag: "ðŸ‡¨ðŸ‡³", name: "Chinese Yuan" },
  HKD: { flag: "ðŸ‡­ðŸ‡°", name: "Hong Kong Dollar" },
  NZD: { flag: "ðŸ‡³ðŸ‡¿", name: "New Zealand Dollar" },
  AED: { flag: "ðŸ‡¦ðŸ‡ª", name: "UAE Dirham" },
  INR: { flag: "ðŸ‡®ðŸ‡³", name: "Indian Rupee" },
  SGD: { flag: "ðŸ‡¸ðŸ‡¬", name: "Singapore Dollar" },
  MXN: { flag: "ðŸ‡²ðŸ‡½", name: "Mexican Peso" },
  BRL: { flag: "ðŸ‡§ðŸ‡·", name: "Brazilian Real" }
};

function App() {
  // Previous state declarations remain the same
  const [fromCurrency, setFromCurrency] = useState('USD');
  const [toCurrency, setToCurrency] = useState('EUR');
  const [amount, setAmount] = useState(1);
  const [exchangeRate, setExchangeRate] = useState(null);
  const [lastUpdated, setLastUpdated] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [historicalRates, setHistoricalRates] = useState([]);
  const [fromInput, setFromInput] = useState('');
  const [toInput, setToInput] = useState('');
  const [showFromDropdown, setShowFromDropdown] = useState(false);
  const [showToDropdown, setShowToDropdown] = useState(false);
  const [currentLanguage, setCurrentLanguage] = useState('en');
  const [currencyRates, setCurrencyRates] = useState({});

  const t = translations[currentLanguage];
  const fromDropdownRef = useRef(null);
  const toDropdownRef = useRef(null);

  // Previous useEffect hooks and fetch functions remain the same

  const filterCurrencies = (input) => {
    if (!input) return Object.keys(CURRENCY_INFO);
    const searchTerm = input.toLowerCase();
    return Object.entries(CURRENCY_INFO)
      .filter(([code, info]) => 
        code.toLowerCase().includes(searchTerm) || 
        info.name.toLowerCase().includes(searchTerm)
      )
      .map(([code]) => code);
  };

  const handleFromInputChange = (e) => {
    const value = e.target.value;
    setFromInput(value);
    setShowFromDropdown(true);
  };

  const handleToInputChange = (e) => {
    const value = e.target.value;
    setToInput(value);
    setShowToDropdown(true);
  };

  const handleFromCurrencySelect = (currency) => {
    setFromCurrency(currency);
    setFromInput(`${CURRENCY_INFO[currency].flag} ${currency} - ${CURRENCY_INFO[currency].name}`);
    setShowFromDropdown(false);
  };

  const handleToCurrencySelect = (currency) => {
    setToCurrency(currency);
    setToInput(`${CURRENCY_INFO[currency].flag} ${currency} - ${CURRENCY_INFO[currency].name}`);
    setShowToDropdown(false);
  };

  // Rest of the previous functions remain the same

  return (
    <div className="app">
      <LanguageSelector />
      <h1>{t.title}</h1>

      <div className="main-container">
        <div className="left-panel">
          <div className="converter-container">
            <div className="currency-inputs">
              <div className="currency-row">
                <div className="currency-input">
                  <div className="currency-select" ref={fromDropdownRef}>
                    <input
                      type="text"
                      value={fromInput}
                      onChange={handleFromInputChange}
                      onClick={() => setShowFromDropdown(true)}
                      placeholder={t.selectCurrency}
                    />
                    {showFromDropdown && (
                      <div className="currency-dropdown">
                        {filterCurrencies(fromInput).map(code => (
                          <div
                            key={code}
                            className="currency-option"
                            onClick={() => handleFromCurrencySelect(code)}
                          >
                            {CURRENCY_INFO[code].flag} {code} - {CURRENCY_INFO[code].name}
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                </div>

                <button className="exchange-button" onClick={handleExchange}>
                  <span className="exchange-icon">â‡„</span>
                </button>

                <div className="currency-input">
                  <div className="currency-select" ref={toDropdownRef}>
                    <input
                      type="text"
                      value={toInput}
                      onChange={handleToInputChange}
                      onClick={() => setShowToDropdown(true)}
                      placeholder={t.selectCurrency}
                    />
                    {showToDropdown && (
                      <div className="currency-dropdown">
                        {filterCurrencies(toInput).map(code => (
                          <div
                            key={code}
                            className="currency-option"
                            onClick={() => handleToCurrencySelect(code)}
                          >
                            {CURRENCY_INFO[code].flag} {code} - {CURRENCY_INFO[code].name}
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              </div>

              {/* Rest of the JSX remains the same */}
              <div className="amount-row">
                <div className="amount-input">
                  <input
                    type="number"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    placeholder={t.amount}
                  />
                </div>
                <div className="amount-result">
                  <input
                    type="text"
                    value={convertAmount()}
                    readOnly
                    placeholder={t.convertedAmount}
                  />
                </div>
              </div>

              <div className="date-row">
                <div className="date-input">
                  <input
                    type="date"
                    value={selectedDate}
                    onChange={(e) => setSelectedDate(e.target.value)}
                    max={new Date().toISOString().split('T')[0]}
                  />
                </div>
                <span className="last-updated">
                  {t.lastUpdated}: {lastUpdated}
                </span>
              </div>
            </div>
          </div>

          <div className="chart-container">
            <h2>{t.historicalRates}: {fromCurrency} {t.to} {toCurrency}</h2>
            {historicalRates.datasets && (
              <Line data={historicalRates} options={chartOptions} />
            )}
          </div>
        </div>

        <div className="watchlist-container">
          <h2>{t.popularCurrencies}</h2>
          <div className="watchlist">
            {Object.keys(CURRENCY_INFO).map(code => (
              <div key={code} className="watchlist-item">
                <span className="currency-code">
                  {CURRENCY_INFO[code].flag} {code}
                </span>
                <span className="currency-name">{CURRENCY_INFO[code].name}</span>
                <span className="currency-rate">
                  {currencyRates[code] ? 
                    `1 USD = ${currencyRates[code].toFixed(4)} ${code}` 
                    : 'Loading...'}
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
